let currentSlide=0;const slides=document.querySelectorAll(".slide");function showSlide(e){slides.forEach((t,n)=>{t.classList.toggle("active",n===e)})}function nextSlide(){currentSlide=(currentSlide+1)%slides.length,showSlide(currentSlide)}function prevSlide(){currentSlide=(currentSlide-1+slides.length)%slides.length,showSlide(currentSlide)}document.addEventListener("DOMContentLoaded",()=>{showSlide(currentSlide)})